const idToken = jwtData.idToken
test('check fixture token with callback', t => {
  const header = {
    alg: 'RS256',
    typ: 'JWT',
    kid: 'NjJEQzRFRDdGMUM3Mzc0QTkyN0IzRjhCRUFEOEI1QkFBQzk4RDhGRA'
  }
  getSigningKey(header, (err, key) => {
    if (err) t.fail()
    // console.log('signingkey', key)
    t.true(key.startsWith('secret'))
  })
  t.pass()
})

test('verify JWT against Auth0 Signing key', async t => {
  t.plan(2)
  try {
    const decoded = await jwtVerify(idToken)
    // console.log(decoded)
    t.is(decoded.nickname, 'avowkind')
  } catch (e) {
    t.fail()
  }

  t.pass()
})

test.serial('Correct signing key for dev tokens', async t => {
  const originalEnv = process.env.NODE_ENV
  process.env.NODE_ENV = 'development'
  getSigningKey({ kid: 'dev' }, (error, key) => {
    process.env.NODE_ENV = originalEnv
    t.is(error, null)
    t.is(key, 'dev')
  })
})

test('verify JWT fails when JWT is expired', async t => {
  await t.throwsAsync(
    async () => {
      await jwtVerify(jwtDataExpired.idToken)
    },
    { instanceOf: TokenExpiredError }
  )
})
